import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Form from './Form';
import UrlBody, { IUrl } from './Downloads'
import axios from "axios";
import { API_URL } from '.';


class App extends Component {

  state = {
    respone_url: [],
  };

  handleSubmit = (urlPassed: string) => {
    this.setState({ response_url: [] })
    console.log(this.state);
    axios.post(API_URL, { 'url': urlPassed }).then((res) => {
      const keys = Object.keys(res.data);
      for (let i = 0; i < keys.length; i++) {
        this.setState({
          respone_url: [...this.state.respone_url, { res: keys[i], url: res.data[keys[i]] }]
        })
      }
    });
    console.log(this.state);
  }

  render() {
    return (

      <div className="App">

        <Form handleSubmit={this.handleSubmit} />
        {(this.state.respone_url.length !== 0) ? <UrlBody url={this.state.respone_url} /> : <div></div>}
        {/* <p>{this.state.url}</p> */}
      </div>
    )
  }
}

export default App;
