import React, { Component } from 'react';

interface PropInterface {
    handleSubmit: (urlPassed: string) => void;
}

class Submit extends Component<PropInterface, any> {

    initialState = {
        url: "",
    }

    state = this.initialState;

    handleChange = (event) => {
        const { url, value } = event.target;
        this.setState({
            [url]: value
        })
    }

    // submitForm = () => {
    //     this.props.handleSubmit(this.state.url);
    //     this.setState(this.initialState)
    // }

    render() {
        const { url } = this.state;

        return (
            <div className="Container">
                <form>
                    <div className="form-group">
                        <label htmlFor="url">URL</label>
                        <input type="text" className="form-control" id="url" aria-describedby="urlHelp" placeholder="Enter URL" value={url} onChange={(event) => this.setState({ url: event.target.value })} />
                    </div>
                    <div className="form-group">
                        <button type="submit" className="mt-2 btn btn-primary" onClick={() => this.props.handleSubmit(this.state.url)}>Submit</button>
                    </div>
                    <small id="urlHelp" className="form-text text-muted">We're open source! Check out our <a href="#">Github</a>!</small>
                </form>
            </div>
        );
    }
}

export default Submit;
