import React, { Component, useEffect, useState } from 'react';

interface PropInterface {
    handleSubmit: (urlPassed: string) => void;
    buttonText: string;
}

// class Form extends Component<PropInterface, any> {
const Form = (props: any) => {
    const [url, setUrl] = useState('');
    const [buttonText, setButtonText] = useState(props.buttonText);
    // initialState = {
    //     url: "",
    //     buttonText: this.props.buttonText,
    // }

    // state = this.initialState;

    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = event.target;
        setUrl(value);
    }

    const submitForm = (event: React.MouseEvent<HTMLButtonElement>) => {
        event.preventDefault();
        props.handleSubmit(url);
    }

    useEffect(() => {
        console.log("here")
        setButtonText(props.buttonText);
    }, [props.buttonText]);

    // useEffect = () => {
    //     console.log("here");
    //     if (this.props.buttonText !== this.state.buttonText) {
    //         this.setState({
    //             buttonText: this.props.buttonText
    //         })
    //     }
    // }

    // render() {

    return (
        <div className="container w-50">
            <form>
                <div className="form-group justify-content-center align-items-center">
                    <label htmlFor="url">URL</label>
                    <input type="text" className="form-control" name="url" aria-describedby="urlHelp" placeholder="Enter URL" value={url} onChange={handleChange} />
                </div>
                <div className="form-group">
                    <button type="submit" className="mt-2 btn btn-primary" onClick={submitForm}>{buttonText}</button>
                </div>
            </form>
        </div>
    );
    // }
}

export default Form;
