import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Form from './Form';
import UrlBody, { IUrl } from './Downloads'
import axios from "axios";
import { API_URL } from '.';


class App extends Component {

  state = {
    respone_url: [],
  };

  handleSubmit = (urlPassed: string) => {
    axios.post(API_URL, { 'url': urlPassed }).then((res) => {
      const keys = Object.keys(res.data);
      this.setState({ response_url: [] })
      for (let i = 0; i < keys.length; i++) {
        this.setState({
          respone_url: [...this.state.respone_url, { res: keys[i], url: res.data[keys[i]] }]
        })
      }
    });
    console.log(this.state);
  }

  render() {
    return (
      <div className="App">
        <div className="dropdown show">
          <a className="btn btn-secondary dropdown-toggle" href="#" role="button" id="dropdownMenuLink" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
            Dropdown link
          </a>

          <div className="dropdown-menu" aria-labelledby="dropdownMenuLink">
            <a className="dropdown-item" href="#">Action</a>
            <a className="dropdown-item" href="#">Another action</a>
            <a className="dropdown-item" href="#">Something else here</a>
          </div>
        </div>
        <Form handleSubmit={this.handleSubmit} />
        {(this.state.respone_url.length !== 0) ? <UrlBody url={this.state.respone_url} /> : <div></div>}
        {/* <p>{this.state.url}</p> */}
      </div>
    )
  }
}

export default App;
